import logo from './logo.svg';
import './App.css';
import Topp,{a} from './component/Top';
import Text from './component/Text';
import { useState } from 'react';
function App() {
  const [mode, setmode] = useState('light')
  const [normalmode, darkmode] = useState({
    color: "black",
    background: "white",
  });
  function onclickdarkmode() {
    if (normalmode.color == "black") {
      darkmode({ color: "white", background: "black", borderColor: "white" });
    } else {
      darkmode({ color: "black", background: "white" });
    }
  }
  function tooglemode(){
    if (mode==='dark'){
    setmode('light');
    document.body.style.backgroundColor = 'white';
    setbtnmode('dark')
    }
    else{
      setmode('dark')
      document.body.style.backgroundColor = 'grey';
      setbtnmode('light')
    }

  }
  return (
    <>
    {/* console.log({a}) */}
    <Topp title="Playing With English" mode={mode} btnmode={btnmode} togglemode={tooglemode}/>
    <div className="container">
    <Text title="Enter Text" mode={mode} btnmode={btnmode} togglemode={tooglemode}/>
    </div>
    </>
  );
}

export default App;
