import { useState } from "react";
import React from 'react'

export default function Text(props) { 

  const [normalmode,darkmode]= useState({'color': 'black',
  'background': 'white'})
  const [btntext, setbtntext] = useState('Enable Dark Mode')
  function onclickupperCase(){
    setText(text.toUpperCase())
    }
  function onclicktitleCase(){
    let titleCase = "";
    text.split(" ").forEach(word => {
      const capitalizedWord = word.charAt(0).toUpperCase() + word.slice(1).toLowerCase();
      titleCase += capitalizedWord + " ";
  });
    setText(titleCase)
    }
  function onclickclearCase(){
      setText('')
        }
        function onclickcopCase(){
          var data=document.getElementById('data')
          data.select();
          navigator.clipboard.writeText(data.value)
          }
    function ontype(event){
      setText(event.target.value)
      }
  function onclickdarkmode(){
    if (normalmode.color=='black'){
    darkmode({'color': 'white',
    'background': 'black',
    'borderColor': 'white'})
    setbtntext('Disable Dark Mode')
    }
    else{
      darkmode({'color': 'black',
      'background': 'white'})
      setbtntext('Enable Dark Mode')
    }
  }
  const [text, setText] = useState("");
        return (
          <>
          <div className="container" style={normalmode}>
          <div>
            <div className="mt-3">
                <h2>{props.title}</h2>
              <textarea className="form-control" id="data" rows="8" value={text} placeholder="" onChange={ontype} style={normalmode}></textarea>
            </div>
            <button className="btn btn-primary mt-3" onClick={onclickupperCase} style={normalmode}>Convert To UpperCase</button>
            <button className="btn btn-primary mt-3 mx-2" onClick={onclicktitleCase} style={normalmode}>Convert To TitleCase</button>
            <button className="btn btn-primary mt-3" onClick={onclickclearCase} style={normalmode}>Clear</button>
            <button className="btn btn-primary mt-3 mx-2" onClick={onclickcopCase} style={normalmode}>Copy</button>
            <button className="btn btn-primary mt-3" onClick={onclickdarkmode}>{btntext}</button>
            </div>
          <div>
            <h2>Summary</h2>
            <p>Words={text.split(" ").length} Char={text.length}</p>
          </div>
          <div>
            <h2>Preview</h2>
            <p>{text}</p>
          </div>
          </div>
          </>
          );
    }
