import { useState } from "react";
import React from 'react'

export default function Text(props) { 
  let dmode={
    'color': 'white',
    'background': 'black',
    'border-color': 'white'
  }
  function onclickupperCase(){
    setText(text.toUpperCase())
    }
    function ontype(event){
      setText(event.target.value)
      }
  const [text, setText] = useState("");
        return (
          <>
          <div className="container" style={dmode}>
          <div>
            <div className="mt-3">
                <h2>{props.title}</h2>
              <textarea className="form-control" id="data" rows="8" value={text} placeholder="" onChange={ontype} style={dmode}></textarea>
            </div>
            <button className="btn btn-primary mt-3" onClick={onclickupperCase} style={dmode}>Convert To UpperCase</button>
            </div>
          <div>
            <h2>Summary</h2>
            <p>Words={text.split(" ").length} Char={text.length}</p>
          </div>
          <div>
            <h2>Preview</h2>
            <p>{text}</p>
          </div>
          </div>
          </>
          );
    }
